mapper.select:
SELECT COUNT(*)
FROM order_item
WHERE order_header_no = #{orderHeaderNo}

SELECT H.ORDER_HEADER_NO, H.ORDER_SDATE, H.CLIENT_NO, CL.CLIENT_NAME, CASE WHEN COUNT(P.PRODUCT_NAME) > 1 THEN CONCAT( MIN(P.PRODUCT_NAME), ' 외 ', COUNT(P.PRODUCT_NAME) - 1, '건' ) ELSE MIN(P.PRODUCT_NAME) END AS PRODUCT_NAMES, SUM(I.AMOUNT) AS TOTAL_AMOUNT, H.EMPLOYEE_NO, E.EMPLOYEE_NAME, H.ORDER_STATUS, H.DELIVERY_STATUS, H.DELIVERY FROM ORDER_ITEM I LEFT JOIN ORDER_HEADER H ON I.ORDER_HEADER_NO = H.ORDER_HEADER_NO LEFT JOIN CLIENT CL ON H.CLIENT_NO = CL.CLIENT_NO LEFT JOIN EMPLOYEE E ON H.EMPLOYEE_NO = E.EMPLOYEE_NO LEFT JOIN PRODUCT P ON I.PRODUCT_NO = P.PRODUCT_NO WHERE 1 = 1
and H.order_sdate = #{orderSdate}
and CL.client_no = #{clientNo}
and P.product_name like concat('%', #{productName}, '%')
and E.employee_name like concat('%', #{employeeName}, '%')
and H.order_status like concat('%', #{orderStatus}, '%')
and H.delivery_status like concat('%', #{deliveryStatus}, '%')
and H.delivery = #{deliveryDate}
GROUP BY H.ORDER_HEADER_NO, H.ORDER_SDATE, H.CLIENT_NO, CL.CLIENT_NAME, E.EMPLOYEE_NAME, H.EMPLOYEE_NO, H.ORDER_STATUS, H.DELIVERY_STATUS, H.DELIVERY ORDER BY H.ORDER_HEADER_NO ASC limit #{amount} offset #{pageStart}

SELECT COUNT(*) FROM ( SELECT H.ORDER_HEADER_NO FROM ORDER_ITEM I LEFT JOIN ORDER_HEADER H ON I.ORDER_HEADER_NO = H.ORDER_HEADER_NO LEFT JOIN CLIENT CL ON H.CLIENT_NO = CL.CLIENT_NO LEFT JOIN EMPLOYEE E ON H.EMPLOYEE_NO = E.EMPLOYEE_NO LEFT JOIN PRODUCT P ON I.PRODUCT_NO = P.PRODUCT_NO WHERE 1 = 1
AND H.order_sdate = #{orderSdate}
AND CL.client_no = #{clientNo}
and P.product_name like concat('%', #{productName}, '%')
and E.employee_name like concat('%', #{employeeName}, '%')
AND H.order_status LIKE CONCAT('%', #{orderStatus}, '%')
AND H.delivery_status LIKE CONCAT('%', #{deliveryStatus}, '%')
AND H.delivery = #{deliveryDate}
GROUP BY H.ORDER_HEADER_NO ) AS subquery

mapper.update:
UPDATE ORDER_HEADER
SET
ORDER_STATUS = #{orderStatus},
DELIVERY_STATUS = #{deliveryStatus},
DELIVERY = #{delivery}
WHERE ORDER_HEADER_NO = #{orderHeaderNo}

UPDATE ORDER_ITEM
SET
PRODUCT_QUANTITY = #{productQuantity},
AMOUNT = #{amount}
WHERE ORDER_HEADER_NO = #{orderHeaderNo}
      AND PRODUCT_NO = #{productNo}
